  /* eslint-disable */
  methods: {
    getRates: function getRates() {
      var _this = this;

      window.setInterval(function () {
        _this.getRatesFromApi();
      }, 10000);
    },
    getRatesFromApi: function getRatesFromApi() {
      var _this2 = this;

      axios.get("https://api.exchangeratesapi.io/latest?symbols=USD,ILS").then(function (res) {
        var usd = parseFloat(res.data.rates.USD);
        var ils = parseFloat(res.data.rates.ILS);
        var rate = ils / usd;

        _this2.$store.dispatch("setRates", rate);
      }).catch(function (error) {//  this.$router.push({
